            let bit = data.val & data.position;
            data.position >>= 1;
            if (data.position == 0) {
                data.position = resetValue;
                if (data.index > length) return '';
                data.val = getNextValue(data.index++);
            }
            res |= (bit > 0 ? 1 : 0) * power;
            power <<= 1;
        }

        let next;
        switch (res) {
            case 0: // 8 ä½å­—ç¬¦
                res = 0;
                maxPower = Math.pow(2, 8);
                power = 1;
                while (power != maxPower) {
                    let bit = data.val & data.position;
                    data.position >>= 1;
                    if (data.position == 0) {
                        data.position = resetValue;
                        if (data.index > length) return '';
                        data.val = getNextValue(data.index++);
                    }
                    res |= (bit > 0 ? 1 : 0) * power;
                    power <<= 1;
                }
                next = String.fromCharCode(res);
                break;
            case 1: // 16 ä½å­—ç¬¦
                res = 0;
                maxPower = Math.pow(2, 16);
                power = 1;
                while (power != maxPower) {
                    let bit = data.val & data.position;
                    data.position >>= 1;
                    if (data.position == 0) {
                        data.position = resetValue;
                        if (data.index > length) return '';
                        data.val = getNextValue(data.index++);
                    }
                    res |= (bit > 0 ? 1 : 0) * power;
                    power <<= 1;
                }
                next = String.fromCharCode(res);
                break;
            case 2: // ç»“æŸ
                return '';
        }

        // åˆå§‹åŒ–å­—å…¸
        dictionary[0] = '';
        dictionary[1] = '';
        dictionary[2] = '';
        dictionary[3] = next;
        let w = next;
        result.push(next);

        // ä¸»è§£å‹å¾ªç¯
        while (data.index <= length) {
            let code = 0;
            maxPower = Math.pow(2, bits);
            power = 1;
            while (power != maxPower) {
                let bit = data.val & data.position;
                data.position >>= 1;
                if (data.position == 0) {
                    data.position = resetValue;
                    if (data.index > length) break;
                    data.val = getNextValue(data.index++);
                }
                code |= (bit > 0 ? 1 : 0) * power;
                power <<= 1;
            }

            if (data.index > length && code === 0) break;

            switch (next = code) {
                case 0: // æ–° 8 ä½å­—ç¬¦
                    res = 0;
                    maxPower = Math.pow(2, 8);
                    power = 1;
                    while (power != maxPower) {
                        let bit = data.val & data.position;
                        data.position >>= 1;
                        if (data.position == 0) {
                            data.position = resetValue;
                            if (data.index > length) return result.join('');
                            data.val = getNextValue(data.index++);
                        }
                        res |= (bit > 0 ? 1 : 0) * power;
                        power <<= 1;
                    }
                    dictionary[dictSize++] = String.fromCharCode(res);
                    next = dictSize - 1;
                    break;
                case 1: // æ–° 16 ä½å­—ç¬¦
                    res = 0;
                    maxPower = Math.pow(2, 16);
                    power = 1;
                    while (power != maxPower) {
                        let bit = data.val & data.position;
                        data.position >>= 1;
                        if (data.position == 0) {
                            data.position = resetValue;
                            if (data.index > length) return result.join('');
                            data.val = getNextValue(data.index++);
                        }
                        res |= (bit > 0 ? 1 : 0) * power;
                        power <<= 1;
                    }
                    dictionary[dictSize++] = String.fromCharCode(res);
                    next = dictSize - 1;
                    break;
                case 2: // ç»“æŸ
                    return result.join('');
            }

            if (dictSize - 1 >= Math.pow(2, bits) - 1) bits++;

            let entry;
            if (dictionary[next]) {
                entry = dictionary[next];
            } else if (next === dictSize) {
                entry = w + w.charAt(0);
            } else {
                return null;
            }

            result.push(entry);
            dictionary[dictSize++] = w + entry.charAt(0);
            w = entry;

